FROM socrata/runit
MAINTAINER Socrata <sysadmin@socrata.com>

RUN apt-get -y update && apt-get install --no-install-recommends -y libyajl2 && rm -rf /var/lib/apt/lists/*;

RUN DEBIAN_FRONTEND=noninteractive apt-get update && apt-get -y --no-install-recommends install nodejs npm && rm -rf /var/lib/apt/lists/*;
# NPM has a hardcoded expired cert, so disable it
RUN npm set ca null
RUN npm install -g n && npm cache clean --force;
RUN n 6.9.1

# LABEL must be last for proper base image discoverability
LABEL repository.socrata/nodejs=""
