FROM library/node:6.10 
RUN apt-get update -y && \
 apt-get install --no-install-recommends -y ruby && rm -rf /var/lib/apt/lists/*;

RUN npm install -g grunt-cli && npm cache clean --force;
RUN npm install -g grunt && npm cache clean --force;
RUN mkdir -p /usr/local/etc \
  && { \
    echo 'install: --no-document'; \
    echo 'update: --no-document'; \
  } >> /usr/local/etc/gemrc;

RUN gem update --system && gem install scss_lint && rm -rf /root/.gem;

# Define working directory. 
WORKDIR /data 

# Define default command. 
CMD ["bash"]
