FROM php:apache
MAINTAINER Movie Masher <support@moviemasher.com>

# install utilities needed by composer
RUN apt-get update && apt-get install --no-install-recommends -y git && rm -rf /var/lib/apt/lists/*;
RUN apt-get update && apt-get install --no-install-recommends -y zlib1g-dev && docker-php-ext-install zip && rm -rf /var/lib/apt/lists/*;

# install composer in /mnt directory
RUN cd /mnt ; curl -f -sS https://getcomposer.org/installer | php;

WORKDIR /mnt/aws
